components.customRuleEvaluator:
var result = {};

if (all("Contains Sun", "Sun (SR) Space", "Contains Space")) {
    result.score = 3;
    result.feedback = "Good Job";
} else if (all("Contains Sun") && any("Contains Space")) {
    result.score = 2;
    result.feedback = "Missing Link";
} else if (any("Contains Sun")) {
    result.score = 1;
    result.feedback = "Missing Space";
} else if (any("Contains Space")) {
    result.score = 1;
    result.feedback = "Missing Sun";
} else {
    result.score = 0;
    result.feedback = "Missing Everything";
}

setResult(result);
